	@Test
	public void createTechniciansTest() {
		// Create an technicians
		Technicians technicians = createMocKTechnicians(Integer.valueOf(100) , TECHNICIANNAME);
		// Recover this technicians
		ResponseEntity<TechniciansResource> result = this.restTemplate.getForEntity("/technicians/1", TechniciansResource.class);
		// The response can't be null
		assertThat(result).isNotNull();
		// The status code must be OK
		assertThat(result.getStatusCode()).isEqualTo(HttpStatus.OK);
		// Response body must not be null
		assertThat(result.getBody()).isNotNull();
		// Assert the Hateoas self link
		Link link = new Link("http://localhost:9999/technicians/1", Link.REL_SELF);
		assertThat(result.getBody().getId()).isEqualTo(link);
		assertThat(result.getBody().getTechnicianName()).isEqualTo(TECHNICIANNAME);
		// Try to create the same technicians, must return a CONFLICT status code
		result = this.restTemplate.postForEntity("/technicians", technicians, TechniciansResource.class);
		// The reponse can't be null
		assertThat(result).isNotNull();
		// The status code must be OK
		assertThat(result.getStatusCode()).isEqualTo(HttpStatus.CONFLICT);
		// Response body must not be null
		assertThat(result.getBody()).isNull();
	}

