		// Test technicianName filter
		result = this.search("/technicians?size=20&technicianName=c");
		// Response body must not be null
		page = result.getBody();
		assertThat(page).isNotNull();
		assertThat(page.getMetadata().getTotalElements()).isEqualTo(1);
